dataset.query:
SELECT b.country1, sum(b.length)
FROM (SELECT country1, length FROM mondial_borders
UNION ALL
SELECT country2, length FROM mondial_borders
) b
GROUP BY b.country1;

SELECT DISTINCT c.name, c.code
FROM mondial_country c, mondial_geo_source g
WHERE c.code = g.country;

SELECT name
FROM mondial_airport
WHERE elevation > 1000.0;

SELECT name
FROM mondial_mountain
WHERE elevation > 8000;

SELECT name
FROM mondial_lake
WHERE area < 5000;

SELECT SUM(area)
FROM mondial_sea
WHERE depth > 1000;

SELECT name
FROM mondial_country
WHERE name LIKE 'B%' AND population > 10000000;

SELECT name
FROM mondial_city
WHERE latitude > 45;

SELECT name
FROM mondial_desert
WHERE area > 10000;
